import { Button, Col, Form, Input, message, Modal, Row } from 'antd';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from "./index.module.css";

import { Campaign } from "../../abi/abi";
import { CampaignFactory } from "../../abi/abi";
import Web3 from "web3";

const web3 = new Web3(Web3.givenProvider);
// Contract address of the deployed smart contract
const contractAddress = "0x64856076A690C195B374cAbA9F570361742C726c";
const campaignFactoryContract = new web3.eth.Contract(CampaignFactory, contractAddress);


export default function SearchList() {

  const [_clist, _setGet] = useState([]);
  const _listGet = async (t) => {
    t.preventDefault();
    var ret_list = [];
    const address_list = await campaignFactoryContract.methods.getDeployedCampaigns().call;
    for (let i=0; i<address_list.length; i++) {
      const campaignContract = new web3.eth.Contract(Campaign, address_list[i]);
      const campaignSummery = await campaignContract.methods.getSummery().call();
      const campaignDescription = campaignSummery[1];
      const balance = web3.eth.getBalance(address_list[i]);
      var tmp = {};
      tmp.id = address_list[i];
      tmp.description = campaignDescription;
      tmp.target = balance;
      ret_list.push(tmp);
    }
    var ret = JSON.stringify(ret_list);
    console.log("list", ret);
    _setGet(ret);
  }

  const [list, setList] = useState(localStorage.getItem("campaigns") ? JSON.parse(localStorage.getItem("campaigns")) : []);
  const [showCreate, setShowCreate] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {

    search();
  }, [])

  const search = async () => {

  }

  const onFinish = async (values) => {
    // create a campaign
    console.log('Success:', values);
    list.push({
      id: new Date().getTime() + "_" + Math.random() * 1000,
      ...values,
      requests: []
    });

    setList([...list]);
    localStorage.setItem("campaigns", JSON.stringify(list))
    message.success("Submit Success");
    console.log("list", list)
    setShowCreate(false);
  }

  const onFinishFailed = async () => {

  }

  const connectWallet = async () => {  //testing purpose
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
  }

  return (<>
    <div className={styles.searchList} >

      <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", backgroundColor: "#000", padding: 20 }}>
        <span style={{ fontSize: 20, fontWeight: "bold", color: "#fff" }}>Home</span>
        <div>
          <Button type='primary' style={{ marginRight: 10, backgroundColor: "#fff", color: "#0000ff" }} onClick={() => {
            setShowCreate(true);
          }}>Create Campaign</Button>
          <Button type='primary' style={{}}onClick={_listGet}>Get</Button>
        </div>
      </div>

      <div className={styles.listItems}>
        {
          _clist.map((item, index) => {
            return <div key={item.id} className={styles.listItem} onClick={() => {
              navigate("/detail/" + item.id);
            }}>
              <div style={{ padding: 10, boxSizing: "border-box" }} >
                <div>{item.description}</div>
                <div style={{ color: "#888", marginTop: 10 }}>{item.target}</div>
              </div>
              <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
                <span>{item.lifespan}</span>
                <Input type='number' onClick={(e) => {
                  e.stopPropagation();
                }}></Input>
                <Button type='primary' onClick={(e) => {
                  e.stopPropagation();
                }}>Button</Button>
              </div>
            </div>
          })
        }
      </div>
      <Modal title="Create Campaign" footer={null} onCancel={() => {
        setShowCreate(false);
      }} open={showCreate}>
        <Form
          name="basic"
          layout="vertical"
          style={{ maxWidth: 600 }}
          initialValues={{ remember: true }}
          onFinish={onFinish}

          onFinishFailed={onFinishFailed}
          autoComplete="off"
        >
          <Form.Item
            label="Description"
            name="description"
            rules={[{ required: true, message: 'Please input description!' }]}
          >
            <Input.TextArea style={{ height: 100 }} />
          </Form.Item>

          <Row gutter="10">
            <Col>
              <Form.Item
                label="Target"
                name="target"
                rules={[{ required: true, message: 'Please input  target!' }]}
              >
                <Input type='number' />
              </Form.Item>
            </Col>
            <Col>
              <Form.Item
                label="Lifespan"
                name="lifespan"
                rules={[{ required: true, message: 'Please input  lifespan!' }]}
              >
                <Input type='number' />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Modal>
    </div >
  </>
  )
}
