import { Button, Form, Input, Modal, message } from 'antd';
import React, { useState } from 'react';
import styles from "./index.module.css";
import { useParams } from 'react-router-dom';

import { Campaign } from "../../abi/abi";
import { CampaignFactory } from "../../abi/abi";
import Web3 from "web3";

const web3 = new Web3(Web3.givenProvider);

export default function SearchList(props) {
  const [list] = useState(JSON.parse(localStorage.getItem("campaigns")));

  const { id } = useParams();
  const [showCreate, setShowCreate] = useState(false);

  const campaignContract = new web3.eth.Contract(Campaign, id);

  const [_detail, _setDetail] = useState([]);
  const _detailGet = async (t) => {
    t.preventDefault();

    var ret_list = [];

    const campaignSummery = await campaignContract.methods.getSummery().call();
    const campaignDetails = await campaignContract.methods.getDetails().call();

    const raiser = campaignSummery[0];
    const campaign_description = campaignSummery[1];
    const campaign_board = campaignSummery[2];
    const contributers_count = campaignSummery[3];
    const numRequests = campaignSummery[4];

    var request_list = [];
    var contributors_detail = [];
    var contributors_list = await campaignContract.methods.getContributers().call();

    for (let i = 0; i < contributors_list.length; i++) {
      var con = {};
      con.address = contributors_list[i];
      tmp.amount = await campaignContract.methods.getContributerAmount();
      contributors_detail.push(tmp);
    }
    for (let i = 0; i < numRequests; i++) {
      var requestSummery = await campaignContract.methods.getRequestSummery().call();
      var requestDetails = await campaignContract.methods.getRequestDetails().call();
      var r = {};
      r.id = i;
      r.request_description = requestSummery[0];
      r.value = requestSummery[1];
      r.start_timestamp = requestSummery[2];
      r.end_timestamp = requestSummery[3];
      r.is_completed = requestDetails[0];
      r.approvalCount = requestDetails[1];
      r.approvalPercentage = requestDetails[2];
      request_list.push(i);
    }

  }

  const [detail, setDetail] = useState(list.find(item => item.id === id));

  const onFinish = async (values) => {
    // create a request
    console.log('Success:', values);
    /*
    detail.requests.push({
      id: new Date().getTime() + "_" + Math.random() * 1000,
      ...values,
    });

    setDetail({ ...detail });
    localStorage.setItem("campaigns", JSON.stringify(list))
    message.success("Submit Success");
    console.log("list", list)
    setShowCreate(false);*/
  }

  const onFinishFailed = async () => {

  }

  return (<>
    <div className={styles.searchList} >

      <h1>{detail.description}</h1>
      <div style={{ backgroundColor: "#eee", padding: 20, borderRadius: 10, maxHeight: 100, overflow: "auto" }}>
        hello 
      </div>

      <div style={{ display: "flex", justifyContent: "space-between" }}>

        <Button type='primary' style={{ width: 160, marginTop: 20 }} onClick={() => {
          setShowCreate(true);
        }}>Create Request</Button>

        <Button type='primary' style={{ width: 160, marginTop: 20 }} onClick={() => {

        }}>Refund</Button>
      </div>

      <div className={styles.listItems}>
        {
          detail.requests.map((item, index) => {
            return <div key={item.id} className={styles.listItem}>
              <div style={{ padding: 10, boxSizing: "border-box" }} >
                <div>Request{index}</div>
                <div style={{ color: "#888", marginTop: 10 }}>{item.description}</div>
              </div>
              <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
                <span>{item.lifespan}</span>
                <Button >Button</Button>
                <Button type='primary'>Approve</Button>
              </div>
            </div>
          })
        }
      </div>

      <Modal title="Create Request" footer={null} onCancel={() => {
        setShowCreate(false);
      }} open={showCreate}>
        <Form
          name="basic"
          layout="vertical"
          style={{ maxWidth: 600 }}
          initialValues={{ remember: true }}
          onFinish={onFinish}

          onFinishFailed={onFinishFailed}
          autoComplete="off"
        >
          <Form.Item
            label="Description"
            name="description"
            rules={[{ required: true, message: 'Please input description!' }]}
          >
            <Input.TextArea style={{ height: 100 }} />
          </Form.Item>

          <Form.Item
            label="Amount"
            name="amount"
            rules={[{ required: true, message: 'Please input  amount!' }]}
          >
            <Input type='number' />
          </Form.Item>

          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit">
              Create
            </Button>
          </Form.Item>
        </Form>
      </Modal>

    </div >
  </>
  )
}
